var point_width = 10;
var point_list = [];
class Point{
  constructor(x,y,tmp = false){
    this.x = x;
    this.y = y;
    if(!tmp){
      point_list.push(this);
    }
  }
  get pos(){
    return this.position;
  }
  get position(){
    return {x:this.x, y:this.y};
  }
  //WIP Still needs to actually snap the point. Not just say snap!
  snap(p){
    if(p === this){
      return false;
    }
    if(this.distance(p) < 10){
      //point_list.push(this);
      this.x = p.x;
      this.y = p.y;
      console.log("Snap!");
      return true;
    }
    return false;
  }
  distance(p,p1=null){
    if(p1){
      return null;
    }
    return Math.hypot(this.x-p.x, this.y-p.y);
  }
  contains(p,p1){

    if(p1){
      // console.log(p);
      // console.log(p1);
      if(this.x-(point_width/2) < p && this.x+(point_width/2) > p &&
        this.y-(point_width/2) < p1 && this.y+(point_width/2) > p1){
          return true;
        }
    }
    if(this.x-(point_width/2) < p.x && this.x+(point_width/2) > p.x &&
      this.y-(point_width/2) < p.y && this.y+(point_width/2) > p.y){
        return true;
    }
    return false;
  }
  draw(canvas,ctx){
    ctx.fillStyle = "#AAA";
    ctx.fillRect(this.x-(point_width/2),this.y-(point_width/2),point_width,point_width);

  }
}


class Line {
  constructor(a,b,lineStyle = "#999"){

    this.lineStyle = lineStyle;
    this.a = a;
    this.b = b;
  }
  draw(canvas,ctx) {
    // ctx.strokeStyle = this.lineStyle;
    // ctx.beginPath();
    // ctx.moveTo(this.a.x,this.a.y);
    // ctx.lineTo(this.b.x,this.b.y);
    // ctx.stroke();
    var mPoint = new Point(Mouse.x,Mouse.y,true);
    if(this.a.contains(mPoint)){
      this.a.draw(canvas,ctx);
    }if(this.b.contains(mPoint)){
      this.b.draw(canvas,ctx);
    }
  }
  get line(){
    return {a:this.a.pos,b:this.b.pos}
  }
  getIntersection(ray){

  	// RAY in parametric: Point + Delta*T1
  	var r_px = ray.a.x;
  	var r_py = ray.a.y;
  	var r_dx = ray.b.x-ray.a.x;
  	var r_dy = ray.b.y-ray.a.y;

  	// SEGMENT in parametric: Point + Delta*T2
  	var s_px = this.a.x;
  	var s_py = this.a.y;
  	var s_dx = this.b.x-this.a.x;
  	var s_dy = this.b.y-this.a.y;

  	// Are they parallel? If so, no intersect
  	var r_mag = Math.sqrt(r_dx*r_dx+r_dy*r_dy);
  	var s_mag = Math.sqrt(s_dx*s_dx+s_dy*s_dy);
  	if(r_dx/r_mag==s_dx/s_mag && r_dy/r_mag==s_dy/s_mag){
  		// Unit vectors are the same.
  		return null;
  	}

  	// SOLVE FOR T1 & T2
  	// r_px+r_dx*T1 = s_px+s_dx*T2 && r_py+r_dy*T1 = s_py+s_dy*T2
  	// ==> T1 = (s_px+s_dx*T2-r_px)/r_dx = (s_py+s_dy*T2-r_py)/r_dy
  	// ==> s_px*r_dy + s_dx*T2*r_dy - r_px*r_dy = s_py*r_dx + s_dy*T2*r_dx - r_py*r_dx
  	// ==> T2 = (r_dx*(s_py-r_py) + r_dy*(r_px-s_px))/(s_dx*r_dy - s_dy*r_dx)
  	var T2 = (r_dx*(s_py-r_py) + r_dy*(r_px-s_px))/(s_dx*r_dy - s_dy*r_dx);
  	var T1 = (s_px+s_dx*T2-r_px)/r_dx;

  	// Must be within parametic whatevers for RAY/SEGMENT
  	if(T1<0) return null;
  	if(T2<0 || T2>1) return null;

  	// Return the POINT OF INTERSECTION
  	return {
  		x: r_px+r_dx*T1,
  		y: r_py+r_dy*T1,
  		param: T1
  	};
  }
}
class Polygon {
  constructor(lines){
    this.lines = lines;
  }
  get obj(){
    var obj_array = [];
    this.lines.forEach(function(val){
      obj_array.push(val.line);
    });
    return obj_array;
  }
  draw(canvas,ctx){
    this.lines.forEach(function(val){
      val.draw(canvas,ctx);
    });
  }
  clickListener(canvas,ctx){

  }
}

function getSightPolygon(sightX,sightY){

	// Get all unique points
	var points = (function(segments){
		var a = [];
		segments.forEach(function(seg){
			a.push(seg.a,seg.b);
		});
		return a;
	})(lines);
	var uniquePoints = (function(points){
		var set = {};
		return points.filter(function(p){
			var key = p.x+","+p.y;
			if(key in set){
				return false;
			}else{
				set[key]=true;
				return true;
			}
		});
	})(points);

	// Get all angles
	var uniqueAngles = [];
	for(var j=0;j<uniquePoints.length;j++){
		var uniquePoint = uniquePoints[j];
		var angle = Math.atan2(uniquePoint.y-sightY,uniquePoint.x-sightX);
		uniquePoint.angle = angle;
		uniqueAngles.push(angle-0.00001,angle,angle+0.00001);
	}

	// RAYS IN ALL DIRECTIONS
	var intersects = [];
	for(var j=0;j<uniqueAngles.length;j++){
		var angle = uniqueAngles[j];

		// Calculate dx & dy from angle
		var dx = Math.cos(angle);
		var dy = Math.sin(angle);

		// Ray from center of screen to mouse
		var ray = {
			a:{x:sightX,y:sightY},
			b:{x:sightX+dx,y:sightY+dy}
		};

		// Find CLOSEST intersection
		var closestIntersect = null;
		for(var i=0;i<lines.length;i++){
			var intersect = lines[i].getIntersection(ray);
			if(!intersect) continue;
			if(!closestIntersect || intersect.param<closestIntersect.param){
				closestIntersect=intersect;
			}
		}

		// Intersect angle
		if(!closestIntersect) continue;
		closestIntersect.angle = angle;

		// Add to list of intersects
		intersects.push(closestIntersect);

	}

	// Sort intersects by angle
	intersects = intersects.sort(function(a,b){
		return a.angle-b.angle;
	});

	// Polygon is intersects, in order of angle
	return intersects;

}

